<script>import AppInput from "~/components/ui/AppInput.vue";import {getItem, setItem} from "~/utils/local.storage.js";export default {    name: "Header",    components: {AppInput},    data: () => ({      territory: '',      mode_key: 'theme-color-mode',      theme_mode: '',    }),    methods: {      changeTheme(){        const newTheme = (this.theme_mode === 'light' ? 'dark' : 'light');        this.theme_mode = newTheme;        setItem(this.mode_key, newTheme);      }    },    mounted() {      this.theme_mode = getItem(this.mode_key) ?? 'light';      this.territory = this.$route.query.q.trim() ? this.$route.query.q : 'Tashkent';    },  watch: {    territory: {      handler(newValue) {        if (newValue.trim()) {          this.$router.push({name: this.$route.name, query: {q: newValue}});        }      }, immediate: true    },  },  }</script><template>  <Body :class="theme_mode">    <header class="header">    <div class="container header-container">      <div class="header-logo">        <img            src="~/assets/images/icons/icon-logo.svg"            alt="logo icon"            class="header-logo__icon"        />        <span class="header-logo__text">          nuxt weather        </span>      </div>      <button          class="header-form__theme-btn"          @click="changeTheme"      >        <img            src="~/assets/images/icons/icon-theme.svg"            alt="theme icon"            class="header-form__theme-icon"        >      </button>      <AppInput          v-model="territory"          placeholder="Выбрать город"          class="header-form__input"      />    </div>  </header>  </Body></template><style lang="scss">  .header{    padding: 20px 0;    background-color: var(--light);    color: var(--dark);    position: fixed;    width: 100%;    top: 0;    left: 0;    z-index: 100;    @include max-md{      padding: 18px 0;    }    @include max-sm{      padding: 15px 0;    }    @include max-xs{      padding: 13px 0;    }    @include max-xxs{      padding: 10px 0;    }    &-container{      display: flex;      justify-content: space-between;      align-items: center;      flex-wrap: wrap;    }    &-logo{      display: flex;      align-items: center;      column-gap: 20px;      @include max-md{        column-gap: 18px;      }      @include max-sm{        column-gap: 15px;      }      @include max-xs{        column-gap: 12px;      }      @include max-xxs{        column-gap: 8px;      }      &__icon{        width: 65px;        height: 65px;        @include max-md{          width: 60px;          height: 60px;        }        @include max-sm{          width: 56px;          height: 56px;        }        @include max-xs{          width: 52px;          height: 52px;        }        @include max-xxs{          width: 46px;          height: 46px;        }      }      &__text{        @include font-size(25);        font-weight: 700;        line-height: 30.48px;        color: var(--color-blue);        text-transform: uppercase;        @include max-md{          @include font-size(23);            line-height: 28px;        }        @include max-sm{          @include font-size(21);          line-height: 24px;        }        @include max-xs{          @include font-size(18);          line-height: unset;        }      }    }    &-form{      &__theme{        &-btn{          margin-left: auto;          width: 35px;          height: 35px;          @include max-xs{            width: 35px;            height: 30px;          }        }        &-icon{          width: 23.33px;          height: 27.71px;          object-fit: contain;          @include max-xs{            height: 23px;          }        }      }      &__input{        margin-left: 20px;        @include max-md{          margin-left: 18px;        }        @include max-sm{          margin-left: 15px;        }        @include max-xs{          margin-left: 12px;        }        @media (max-width: 530px) {          margin-left: unset;          margin-top: 10px;          width: 100%;        }      }    }  }</style>