<script>export default {  props: {    modelValue: {      type: String,      required: true    },    value: String,    placeholder: String,    lazy: Boolean  },  emits: ['update:modelValue'],  computed: {    computedModelValue: {      get() {        return this.modelValue      },      set(newValue) {        this.$emit("update:modelValue", newValue);      }    }  }}</script><template>  <div class="app-input">    <input        v-model="computedModelValue"        class="app-input__input"        :placeholder="placeholder"    />  </div></template><style lang="scss">.app-input{  &__input{    width: 100%;    border-radius: $border-10;    padding: 9px 20px;    height: 37px;    font-weight: 500;    @include font-size(16);    line-height: 19.5px;    background-color: $light-blue;    @include max-xs{      padding: 8px 15px;      @include font-size(15);      line-height: unset;    }    @include max-xxs{      height: 35px;      @include font-size(14);    }  }}</style>